import React, {
    Component
} from 'react';
import './App.css';

const PlayerCard = ({
    color,
    symbol
}) => {
    const style = {
        backgroundColor: color,
        backgroundImage: "url(./img/" + symbol + ".png)"
    }
    return ( <div style = {style} className = "player-card" >
                          {symbol} 
              </div>
    )
}

class App extends Component {

    constructor(props) {
        super(props)
        this.symbols = ["rock", "paper", "scissors"]
        this.state = {}

        decideWinner = () => {
            const { playerBlue, playerRed } = this.state
            if (playerRed == playerBlue) {
                return "It's a draw!";

            } if ((playerRed === "rock" && playerBlue === "scissors")||
                  (playerRed === "paper" && playerBlue === "rock")||
                  (playerRed === "scissors" && playerBlue === "paper")) {
                return "Red Player Wins !"

            }
                return
        }

    }
    runGame = () => {
        let counter = 0;
        let myInterval = setInterval(() => {
            counter++
            this.setState({
                playerRed: this.symbols[Math.floor(Math.random() * 3)],
                playerBlue: this.symbols[Math.floor(Math.random() * 3)]

            })
            if (counter > 40) {
                clearInterval(myInterval);
                this.setState({winner: this.decideWinner()})
            }
        }, 100)
    }
    render() {
        return ( <div className = "App" >
            < PlayerCard color = "red" symbol = { this.state.playerRed }/>
            < PlayerCard color = "blue"symbol = { this.state.playerBlue }/>
            <p>{this.state.winner}</p>
            < button onClick = {this.runGame} > Run Game </button> </div>
        );
    }
}

export default App;
